INSERT INTO `recepten` (`receptid`, `titel`, `temp`, `soort`, `ingred`, `personen`, `tijd`, `diff`, `uitgeprobeerd`, `vooraf`, `vriezen`, `wijn`, `trefwoord`, `bereiding`, `bron`, `tip`) VALUES
(13, 'Cataplana met garnalen', 'warm', 'vis', '250 gepelde garnalen\r\n1 rode paprika\r\n1 gele paprika\r\n1 courgette\r\n2 uien\r\n1 busseltje koriander\r\nolijfolie', '4', 0, 30, '', 'nee', 'nee', 'segada 2001 rode portugese wijn', '', 'de paprikas op voorhand in reepjes en de courgettes in schijfjes snijden\r\nde uien snijden en ze laten garen op een gematigd vuurtje in een bodem van olijfolie in de open cataplana\r\nde volgende ingredienten per laag toevoegen:\r\n1 laag schijfjes courgette\r\n1 laag garnalen\r\n1 laag gele paprikas\r\n1 laag garnalen\r\n1 laag rode paprika\r\nkruiden met peper en zout\r\nde blaadjes koriander erbovenop leggen\r\nde cataplana sluiten en 15 tot 20 min laten garen op een matig vuurtje.\r\nopgelet voor de vrijkomende dampen bij het ophefen van het deksel.', '', ''),
(12, 'tandoori kippenbrochettes', 'warm', 'vlees', '300 gr kippenhaasjes\r\n2 eetlepels tandoori poeder\r\n2 potjes griekse yohurt\r\n20 blaadjes munt\r\n2 takjes koriander\r\nolie en zout', '4', 20, 20, '', 'nee', 'nee', '', '', 'rijg de kippenhaasjes op kleine houten brochettes\r\nmeng 1 potje yoghurt met de tandoori-poeder,voeg een snuifje zout toe en strijk de brochettes in met deze mengeling.zet de brochettes op een koele plaats.\r\nmeng de andere yoghurt met de muntblaadjes en enkele blaadjes koriander{ bewaar enkele blaadjes voor de garnering} en voeg wat zout toe.\r\nverwarm in een grote pan een beetje olie en bak de brochettes erin,ongeveer 2 min aan elke kant.\r\ndien bij deze brochettes de yoghurtsaus op.\r\n\r\ntip: je kunt deze brochettes opdienen als voorgerecht ,samen met een salade.', NULL, NULL),
(10, 'duivels kalkoenstoofpotje', 'warm', 'vlees', 'benodigdheden:voor 4 pers. 800 gr kalkoenstoverij{uitgebeende bil in blokje},1 bakje verse champignons,boerenboter,2 afgestreken eetlepels maizena,sap van 1/2 citroen,zakje kalkoenkruiden {gratis bij je slager},1 potje room,1 duvel biertje, 1/2 liter volle melk, gehakte peterselie duvel..', '4', 0, 30, '', 'ja', 'ja', '', '', 'kruid het vlees met de inhoud van het zakje\r\nborstel de champ ,deel ze in 4 en besprenkel met citroensap\r\nbruin het vlees in de boter en voeg de champ toe\r\ndraai het vuur op een laag pitje,giet er de melk en het bier bij en laat een uurtje zachtjes sudderen met het deksel erop,\r\nregelmatig omroeren om aanbranden te voorkomen.\r\nbind met de maisbloem,voeg er de room aan toe,en laat nog een half uurtje zachtjes inkoken,zonder deksel op de pot.\r\nop het laatst roer je hier enkele lepels gehakte peterselie doorheen.\r\n\r\ntip 1: het lijkt alsof er te veel saus is in verhouding met het vlees,maar dit stoofpotje maak je de dag voordien zodat het vocht terug door de vleesvezels wordt opgenomen...\r\ntip 2: geef er een puree van woteltjes en aardappelen bij, en niet vergete: een schuimende duvel.', NULL, NULL),
(11, 'varkenshaasje met tomatensaus', 'warm', 'vlees', '350 gr varkenshaasje,2 eetlepels olijfolie,50 gr boter,1 ui,300 gr mix bospaddestoelen,1,5 dl tomatensap,1/2 dl witte blsamico azijn of wittte wijnazijn,1 eetlepel honing,1 koffielepel mosterd,1 teentje knoflook,1 bosje basilicum,scherpe parika-tandoripoeder,peper,zout\r\n', '2', 0, 20, '', 'nee', 'ja', '', '', 'het varkenshaasje:\r\nlaat het varkenshaasje een kwartiertje bakken in boter. Draai het halfweg wel eens om\r\nBestrooi het met zout,peper en wat scherpe paprika.\r\n\r\nDe tomatensaus:\r\nsnij de ui in halve ringen\r\nVerhit de olie en laat de ui 5 minuten zachtjes bakken.\r\nPers de knoflook uit boven de olie.\r\nRoer tomatensap, honing,azijn,mosterd ,zout en scherpe paprika erdoor.\r\nLaat het geheel ongeveer 15 min zachtjes inkoken. ergeet niet  af en toe te roeren.\r\nBak ondertussen de bospaddestoelen en kruid ze met peper en zout.\r\nServeer het vlees op een onderlaag van de bospaddestoelen te midden van de tomatensaus.Werk af met enkele takjes basilicum.\r\n', NULL, NULL),
(14, 'tongrolletjes met normandische saus', 'warm', 'vis', '8 kleine tongrolletjes { 400 gr}\r\n1 eetlepel becel bakken en braden\r\n1,5 eetlepel bloem,200 ml visbouillon, 3 eetlepels becel voorde koffie\r\n1 eetlepel citroensap,peper,zout,snuifje cayennepeper.\r\n100 gr gestoofd champignons in plakes met champignonnat\r\n20 gr gare  mosselen', '', 0, 40, '', 'nee', 'nee', '', '', 'laat de vetstof heet worden in sauspan.roer de bloem eronder,roerend opdrogen zonder kleuren.\r\nvoeg geleidelijk  aan de visbouillon erbij,blijf roeren met klopper om klonters te voorkomen. en laat enkele minuten koken.\r\nroerde becel voor de koffie erdoor.\r\nvoeg de champignons en mosselen erbij\r\nstoom de tongrolletje gaar boven een bodempje water, { ook in mgolf??},voeg de saus erbij.\r\nbreng op smaak met citroensap.', NULL, NULL),
(16, 'wijtingfilets op een bedje van paprikas', 'warm', 'vis', '800 gr wijtingfilets\r\n3 paprikas {groen,geel rood}\r\n1 courgette,1 ui,4 teentjes look,1 eetlepel tapenade\r\n5 cl witte wijn.10 cl gezeefde tomaten,provencaalse kruiden, 1/4 de liter verse room,rozemarijn,zen p.', '4', 40, 40, '', 'nee', 'ja', '', '', 'was en snijd de paprikas en de courgette in kleine blokjes.hak de uien 2 teentjes look.\r\nfruit de look en de ui in een beetje olijfolie,voeg de paprikas,de courgette en de tapenade toe.voeg de witte wijn,de provencaalse kruiden,de gezeefde tomaten en de room toe,breng op smaak met p en z en laat 10 minuten sudderen op een zacht vuur.\r\nverwarm deoven voor op 180 graden.\r\nstrooi p en z op de filets,plet de resterende look en wrijf de filets ermee in. bestrooi de filets met rozemarijn. rol de wijtingfilets op en maak de rolletjes vast met een tandenstoker. leg de rolletjes in een geoliede ovenschaal,giet er een beetje olijfolie over en laat ze 20 min bakken in de oven.\r\nschik de rolletjesop een bedje van paprikas en garneer met verse kruiden.\r\ndien bij dit gerecht in olie gebakken aardappeltjes op die bestrooid zijn met mandarijn\r\n\r\ntip:deze paprikabereiding past uitstekend bij tal van vis en vleesgerechten.', NULL, NULL),
(17, 'blanke botersaus', 'warm', 'vis', '1 DL WITTE WIJN\r\n150 GR MALSE BOTER\r\n1 FIJNGESNEDEN SJALOT\r\nSAP VAN 1/2 CITROEN\r\nP EN Z', '4', 20, 30, 'nee', 'nee', 'nee', '', '', 'DE WITTE WIJN SAMEN MET DE SJALOT TOT 1/3 INKOKEN.\r\nZEVEN en van het vuur de malse boter er geleidelijk onder roeren.\r\nop smaak brengen met p, z, en citroensap', '', 'SAP VAN 1/\r\n\r\n\r\n'),
(20, 'gehaktrolletjes', 'warm', 'vlees', '2 teentjes knoflook,1 rode peper, 1 bosje lenteuitjes,300 gr varkensgehakt,3 eetlepels vissaus,1 theelepel sesamolie,2 eetlepels fijngehakte koriander,1 pakje filodeeg,2 eetlepels arachideolie.\r\n', '4', 0, 30, 'nee', 'nee', 'nee', '', '', 'de knoflook pelle, en fijnhakke.\r\nde peer in de lengte halveren,ontpitte,afspoelen en fijnhakken.\r\nde lenteuitjes schoonmaken,wwassen en in kleine stukjes snijden.\r\nde groenten samen met het gehakt,de vissaus ,de sesamolie en de koriander in een schaal door elkaar mengen.\r\n\r\nhet deeg in grote rechthoeken van ongeveer 8 bij 15 cm snijden,deze naast elkaar leggen en met arachideolie bestrijken. vervolgens telkens 2 deegvierkanten op elkaar leggen.\r\n de bakoven op 225 c voorverwarmen.het gehaktmengsel over het deeg verdelen,in de lengte oprollen en op een ingevette bakplaat leggen,\r\nin de bakoven ongeveer 8 tot 10 minuten goudbruin laten bakken.\r\n\r\n\r\nserveertip: een ideaal knabbeltje als aperitief.\r\nsnij de warme rolletjes middendoor en doe de chilisaus in een schoteltje en serveer als een dipsausje.\r\nu kan de gehakt rolletjes ook serveren met een zachte mosterd.\r\n\r\nfish saus santa maria 200 ml o cool\r\nsweet hot chili saus santa maria 200 ml o cool\r\n\r\n\r\n\r\n\r\n\r\n', 'o cool', ''),
(24, 'veloutesoep van avocado met grijze garnalen.', 'warm', '', '2 dikke rijpe avocados of drie kleine\r\n1 limoen,125 gr gepelde grijze garnalen,40 gr boter,40 gr bloem,1/2 dl melk,1 blokje gevogeltebouillon,croutons,cayennepeper, z en p.', '4', 25, 30, 'nee', 'nee', 'nee', '', '', 'laat de boter zachtjes smelten in de pan.bestrooi met bloem en meng met een garde.voeg er beetje bij beetje de melk bij terwijlu blijft roeren. Voeg er eveneens 1/2 l water bij en het bouillonblokje.Breng aan de kook en laat zacht sudderen.\r\nintussen snijdt u de avocados in 2.haal de pit eruit en snij het vruchtvlees in kleine blokjes.\r\nbesprenkelmet een vleugje limoensap en mix alles tot u een glad mengsel krijgt.\r\ngiet een lepel soep over de avocadoroom en meng.\r\ngiet zo heel de soep,beetje bij beetje,over deroom tot u een homogene roomsoep krijgt.\r\nzet de pan terug op het vuur en doe er de grijze garnalen bij.breng op smaaken kruid extra met een mespuntje cayennepeper.\r\ndien warm op met de croutons.', '', ''),
(29, 'chop suey in bamboe', 'warm', 'vlees', '300 gr varkensvlees van de bil,spiering of lage ribben\r\nolijfolie,sojasaus, 1 eetlepel sherry\r\n20 gr maiszetmeel,125 gr vermicelli,125 bamboescheuten uit blik,\r\n125 gr champignons,125 gr wit van prei,1 snuifje gemberpoeder,zout\r\n', '2', 10, 30, 'nee', 'nee', 'nee', '', 'chinees', 'kook de vermicelli gaar in licht gezouten water.\r\nmaak de groenten schoon.Halveer de champignons.Snij de bamboescheuten. en prei in fijne reepjes.\r\nverdeel het vlees in kleine blokjes.\r\nstoof deze ingredienten gedurende enkele minuten knapperig gaar in hete olie.\r\nroer daarna sojasau,gember,sherry en het bindmiddel door de olie.breng op smaak met een snuifje zout.\r\nmeng nu alle ingredienten en de saus in een dienschotel en laat het geheel eventjes trekken.\r\n\r\nchop suey moet heet zijn als u hem opdient.en hij wordt pas echt chinees als u alle ingredienten zeer fijn snijdt en kwistig omspringt met sojasaus en de gemberpoeder.', 'krant wvl', ''),
(22, 'varkensreepjes met slagroom', 'warm', 'vlees', '300 gr lage rib of spiering,75 gr boter,1/2 dl room,50 gr wortelen,150 gr erwtjes,2 tomaten,2 teentjes knoflook,1 theelepel kerriepoeder,1 theelepel cayennepeper, 0,5 theelepel kurkumapoeder, zout.', '2', 15, 30, 'nee', 'nee', 'nee', '', '', 'schil de wortelen,snij ze in schijfjes en kook ze 1 minuut voor in licht gezouten water.\r\nspoel de wortelen daarna onder koud stromend water.\r\nsnij het vlees in reepjes en bestrooi ze met zout en de helft van de kerrie-kurkumapoeder en de cayennepeper.\r\nontvel de tomaten,verwijder de zaadjes en snij de tomaten in stukjes.\r\nverhit de boter in een braadpan,voeg de geplette knoflookteentjes toe en roerbak het vlees gedurenede 3 minuten.\r\nvoeg de erwtjes en de worteltjes toe,samen met de room,de tomaten en de rest van de kerrie en de cayennepeper.\r\nlaat alles met het deksel op de pan nog tien  minutenstoven', '', ''),
(26, 'brusselse wafels', 'warm en koud', 'andere', '1/ brussels wafels dus van die grote welke meter soms bakte. er komen er\r\nongeveer 60 uit een kilo bloem tenminste als ze niet teveel plakken...\r\nbenodigdheden: 1kgr bloem,80 gr gist,400 gr gesmolten boter, 1 liter melk, 1\r\nliter water,8 eieren,vanille essence { niet noodzakelijk} 10 gr zout;\r\n', '', 0, 30, 'ja', 'ja', 'ja', '', '', 'breek de gist in 1/4 liter lauw water.Maak een kuiltje{val er\r\nniet in} in de bloem,roer de gist eronder,vervolgens de lauwe melk en de\r\neierdooiers.Daarna beurtelings nog 3/4 lauw water,de gesmolten boter,de 8\r\nstijfgeslagen eiwitten en het zout bijvoegen.\r\nLaat het deeg rijzen{ 1/4 uur in warmte} en bak in een dik wafelijzer.Het\r\nijzer moet niet ingesmeerd worden.Dien warm op met bloemsuiker of\r\nslagroom....\r\n', '', ''),
(27, 'bewaarwafeltjes', 'warm en koud', 'andere', 'Bewaarwafeltjes: die kleinere ,harde warmen koud te eten.\n\nBenodigdheden: \n1/2 kg solo boter{volgens auteur= banketbakker van het recept moet het wel solo zijn}.heb zelf altijd solo gebruikt...\n1kg bloem,\n4 pakjes vanillesuiker\n700 gr griessuiker\n8 eieren\nsnuifje zout\n\n', '', 0, 30, 'ja', 'ja', 'ja', '', '', 'meng de eierdooiers met de griessuiker en voeg er dan de zacht\ngesmolten{ niet te warm*** vb micogolfoven kort}solo aan toe.\nmeng alles goed doorheen, is tamelijk stijf van deeg.\nDan mengen we de gezeefde bloem erdoor.\nDoe dan de vanillesuiker en het zout erbij.Maak een homogeen mengsel { dus\ntamelijk vast} We kloppet eiwit stijf en spatelen dit in het mengsel.\n\nOnmiddellijk bakken in gewoon wafelijzer.\n', '', ''),
(28, 'kalkoentournedos met curry', 'warm', 'vlees', '4 kalkoentournedos\r\n\r\n1 gehakte ui,het vlees van 2 rijpe tomaten,1 soeplepel curry,1 geplet teentje knoflook,1 in schijfjes gesneden banaan.boter-zout', '4', 0, 20, 'nee', 'nee', 'nee', '', '', 'boter of olie verwarmen in de pan,de kktournedos aan bede kanten mooi bruin bakken. ze uit de pan nemen en warm houden.\r\nde fijngehakte ui fruiten in de braadpan,voeg er de geplette tomaten,het geplette knoflookteentje en de curry aan toe. goed mengen.\r\nvoeg er de tournedos aan toe en laat nog 15 min zacht sudderen,met deksle op de pan.\r\n5 min voor het einde van de kooktijd voeg er de schijfjes gesneden banaan bij. serveer met rijst.', '', ''),
(30, 'echte appeltaart', 'warm en koud', '', '2 eieren,2 pakjes vanillesuiker,4 eetlepels maisolie,8 lepels fijne suiker,10 lepels zelfrijzende bloem,6 lepels melk ,2 appels in schijfjes gesneden.', '4', 0, 20, 'nee', 'nee', 'nee', '', '', 'meng de eieren,vanille,olie,suiker en emlk goed onder elkaar met een keukenrobot of gewoon met een klopper.\r\nspatel voorzichtig de bloem erdoorheen,let erop dat er geen klontertjes gevormd worden.\r\nlaat een kwartiertje rusten\r\nmeng er daarna de appelschijfjes onder\r\ngiet dit mengsel in een goed ingeboterde,platte taartvorm.\r\nafbakken in een voorverwarmde oven op 160 graden gedurende 30 min\r\n\r\n\r\n\r\ntip:wil je dit met meer zwier serveren,dan leg je een half afgekoeld stukje taart op een groot bord,schep er een bolletje vanilleijs bij en werk af met een toefje slagroom.je kan dit ook nog bestrooien met poedersuiker.', 'kr w vl', ''),
(31, 'kalkoenrolletjes met vulling in mg', 'warm', 'vlees', '4 platte kalkoenschnitzels van 200 gr\r\nzout en peper\r\n4 plakken gekookte ham, 1 ui,1 eetlepel franse mosterd,1 eetlepel tomaten ketchup,2 eetlepels amandelschaafsel, 2 eetlepels rozijnen,1 bolletje gember,1 snuifje kerrie,1 snuifje cayennepeper, een paar druppels brandewijn,2 eetlepels fijngehakte peterselie,braadvet.\r\nen verder: 1 kopje witte wijn,1/4 liter vleessaus,1/2 bekertje creme fraiche,1 mespunt saffraan,een paar druppels tabasco,1/2 bosje dragon.\r\n', '4', 0, 30, 'ja', 'nee', 'nee', '', 'rolletjes in microgolfoven', 'was de schnitzel,dep ze droog en sla ze zo nodig nog platter{ ik snijd dit in fijne schelletjes met machine wanneer het vlees nog voor de helft ingevrozen is = brokkelt niet\r\nwrijf ze in met z en p. en leg er een plakje ham op.\r\nhak de ui zeer fijn, en vermeng met de mosterd, tom keytchup,amandelen,rozijnen en de fijngehakte gember.\r\nvoeg de kerrie,cayennepeper en brandewijn { ik niet } toe ,is straf genoeg\r\nen maak de smaak af met  z en p en schep de fijngehakte peterdelie erdoor.\r\nbe nogmaals op smaak af,schep de fijngesneden dragon erdoor en ietstrijk de filets met deze massa.\r\nrol de filets op en bind ze vast met keukentouw\r\ndoe het vet in de braadslee, en zet dit 2 tot 3 min ,bij vol vermogen in MG.\r\nleg de rolletjes erin en braad ze 8 tot 10 min bij vol vermogen\r\ngiet de witte wijn en de vleessaus erbij\r\nroer de creme fraiche erdoor en voeg zout,peper,saffraan en tabasco toe.\r\nzet dsit nog 15 tot 20 min bij 75 ptocent vermogen afgedekt in mg\r\nschik de rolletjes op een schaal en beter nog snij de rolletjes eerstin plakjes,maak de saus\r\naf schep de fijngesneden dragon erdoor en giet dit over de rolletjes.\r\n\r\nnb. indien gebrek aan een of ander kruid hier is het geen probleem want het is straf genoeg', 'kookboekje  microgolfkookboek', ''),
(32, 'brocolli puree', 'warm', '', 'mix een gare broccoli { roosjes en stengel} met drie gare aardappelen en u hebt een lekkere puree.\r\nwel goed op smaak brengen met p,z, en knoflook.', '', 0, 20, 'nee', 'ja', 'ja', '', 'puree', '', 'zondagskrant', ''),
(33, 'kip goulash met champignons', 'warm', 'vlees', '500 gr kipfilet,1 prei,1 rode en 1 gele paprika,1 rood pepertje,1 teentje knoflook,gehakte bieslook,1 blikje tomatenpuree,{ 140 gr}  250gr verse champignons,1/2 liter water,1 kippenbouillonblokje,1 koffielepel paprikapoeder,1 koffielepel origano, scheut olie.\r\n\r\n\r\n', '4', 0, 20, 'nee', 'ja', 'nee', '', 'light', 'kook het water met het bouillonblokje.Leg er de kipfilets in,laat weer tot het kookpunt komen en 10 min laten trekken van het vuur af.\r\nreinig de parikas,het pepertje en de prei.Snij deze in fijne reepjes.Verhit de olie in een groet braadpan en stoof de groentjes met het parikapoeder en pers er het teentje knoflook in>Borstel de champignons,snij ze in 4 en stoof ze mee.oeg de tomatenpuree toe met de oregano.\r\nBlussen met de verkregen kippe bouillon en 10 min laten sudderen.Versnij de filets in blokjes en voeg deze 5 minuutjes voor het opdienen erbij.#\r\nserveren in een grote platte schotel en bestrooien met de gehakte bieslook.\r\n\r\ntip: als je zoals alle sudderpotjes de goulash de dag vooraf maakt,hoef je geen peper en zout meer toe te voegen,omdat de smaken al meer dan genoeg tot uiting komen.\r\n\r\ntip 2/een scheutje limoensap geeft een extra fris accent.', 'kr w vl', ''),
(34, 'Spaghetti op thomas wijze', 'warm', 'vlees', '* Pakje fijne spaghetti\r\n* 500gr varkens en rund gehakt, liefst van op de hoek van Parkstraat met Maria-Theresiastraat\r\n* groenten (wortels,...) naar keuze\r\n* een pot spaghetti saus (400ml zeker?), die van den Aldi is goed genoeg\r\n* een ui\r\n* spaghettikruiden', '3', 20, 30, 'ja', 'ja', 'ja', '', 'spaghetti', 'Zet pot1 warm water op stoof en als het water goed kookt voeg je de strookjes toe.\r\n\r\nBak ui goudbruin in pan1, kap het rundsgehakt erbij fijnpletten met vork, voeg wat peper toe.\r\nVoeg de pot saus eraan toe.\r\nRustig laten sudderen en wat kruiden toevoegen.\r\n\r\nSmijt de vulling uit pan1 in pot 1. Goed roeren (verboden fijn te snijden). Mond open en binnen ketsen :-)\r\n\r\nsmakelijk', 'uit het koppie', ''),
(35, 'amandel- bladergebak', 'warm en koud', '', '300 gr bladerdeeg\nvulling: 100 gr boter, 2 eieren,150 gr bloemsuiker,125 gr amandel-of kokospoeder.\nvariant vulling: 150 gr boter,175 gr griessuiker,50 gr bloem,75 gr kokospoeder,75 gr amandelpoeder, 1 koffielepel bakpoeder, 2 eieren.\n', '', 0, 40, 'ja', 'ja', 'ja', '', '', 'bestrijk de taartvorm met boter en spoel onder koud water.    rol 2/3 van de bladerdeeg cirkelvormig uit en leg het in de taartvorm.\r\ndoe de boter in een diepe kom en klop  luchtig op met een electrische garde. doe er de eieren een voor een bij,de bloemsuiker en het amandel of kokospoeder.\r\nverdeel het bereide mengsel over het bladerdeeg.\r\nrol de rest van het bladerdeeg in een rechthoek uit.snijd in fijne reepjes metbehulp van een rolmesje of een scherp  mes en een meetlat???? vlecht de reepjes in ruitvorm over de taart en breng een bredere reep  langs de rand van de taartvorm aan.\r\nbak de taart in de oven,ontvorm ze en laat afkoelen op een rooster.\r\n\r\nvarant : onder de laag frangipane kunt u ook een laagje uitgelekte krieken aanbrengen { 600 gr}\r\nindien u de randen van het deeg wat breder maakt,kunt u ze naar binnen vouwen    na de vulling te hebben aangebrqcht.\r\n\r\nvariant vulling:Klop de boter luchtig op,doe er de eierdooiers bij, de griessuiker,het kokos en amandelpoeder.Roer krachtig tot er een homogene massa ontstaat.Klop de bloem en het bakpoeder onder het deeg. Spatel het stijfgeklopte eiwit door de massa.', '', 'tadaaa en tis klaar'),
(36, 'meringues of schuimpjes', 'koud', '', '8 eiwitten, 500 gr bloemsuiker, boter\r\n', '', 0, 40, 'ja', 'ja', 'nee', '', '', 'klop het eiwit zeer stijf\r\nroer zeer vorzichtig de bloemsuiker erdoor { het eiwit mag niet in elkaar klappen }\r\nbestrijk de ovenbakplaat met boter en spuit het eiwit met een spuitzak met gekarteld mondstuk erop als in elkaar lopende roosjes.\r\nBak in de oven.\r\n\r\nvariant : meringue met koffie: doe er 1 koffielepel oploskoffie bij per 4 eiwitten\r\n\r\n              mer met chocolade: doe er 1 koffielepel cacaopoeder bij per 4 eiwitten\r\n\r\n              mer met frambozen: doe er 1 koffielepel aromapoeder met frambozensmaak bij per 4 eiwitten.\r\n', '', ''),
(37, 'gevogeltesaus', 'warm', 'andere', 'zie onderport', '4', 5, 20, 'nee', 'nee', 'nee', 'port', 'snel', 'bak de champignons met de spekblokjes en de ingesnipperde uitjes.\r\nvoeg er de rode port toe, laat even inkoken.', 'zondagskrant', ''),
(38, 'rijsttaart', 'warm en koud', '', '2 kopjes melk\r\n1 kopje zelfrijzende bloem\r\n1 kopje suiker( iets minder)\r\n2 pakjes vanillesuiker\r\n1 doos BOSS rijst ( smaak natuur)\r\n4 eieren ( dooiers)\r\n', '4', 0, 20, 'nee', 'nee', 'nee', '', 'snel', 'alles doorheen mengen\r\nwit kloppen en eronder mengen\r\n\r\noven op 200 graden, na een half uur verminderen op 150 graden\r\nna een half uur op 150 graden oven afzetten en nog 10 minuten laten instaan.\r\n( de taartvorm goed vet maken en bestrooien met bloem )\r\ndoorsnee 30 cm\r\n', '', ''),
(39, 'koteletten met paprikas', 'warm', 'vlees', '2 varkenskoteletten\r\n75 gr boter\r\n1,5 paprika\r\n50 gr champignons\r\n1/2 ui\r\n1/2 blik gepelde tomaten\r\n100 gr erwtjes\r\n1 knoflookteentje\r\ngehakte peterselie\r\ngedroogde oregano\r\npeper,zout\r\n', '2', 20, 20, 'nee', 'nee', 'nee', '', 'paprikas', 'leg de parikas onder de grill tot ze zwartgeblakerd zijn.\r\nspoel,pel en ontpit ze\r\npel en hak de ui en het knoflookteentje\r\nfruit alles samen met de gepelde tomaten en erwtjes in de boter. kruid met p en z.  bestrooi met oregano en peterselie en laat 20 min sudderen.\r\n\r\nmaak de champignons schoon en snij ze fijn. vieg ze na 5 minuten kooktijd bij de rest.\r\n\r\nrooster ondertussen de varkenskoteletjes 5 tot 6 min aan elke kant. serveer met de saus.', 'krant w vl', ''),
(41, 'broodrecept', 'warm en koud', '', 'zie onder', '4', 120, 20, 'ja', 'ja', 'ja', '', 'brood', '1) maatlepeltje (dat hoort bij broodmachine) en weegschaal nemen\r\n \r\n2) 2,5 kleine maatlepel korrelgist onderaan in (droog) bakblik doen\r\n \r\n3) 250 GRAM grof meel (vb. volkoren) op de gist doen + aanvullen met witte bloem tot 610 à 630 gram in totaal.  \r\n \r\n4) grote maatlepel suiker (liefst bruin) + kleine maatlepel zout langs de rand van de bloem strooien\r\n \r\n5) grote maatlepel olie (liefst olijfolie) in putje in midden van de bloem doen\r\n \r\n6) 300 à 350 ml water  (lauw) op alles doen en machine in gang zetten (begint te kneden dan) : begin eerst met 300 ml water, en kijk na 5 minuten kneden of het niet te droog is.   Voeg dan eventueel nog wat water bij (geleidelijk).  Na 10 minuten kneden moet de bol deeg een mooi rond bolletje vormen, niet te nat en niet te droog. \r\n \r\nWij bakken hier elke week zo''n 3 broodjes, en het lukt altijd (tenminste met "normale" meelsoorten en bloem, niet met soja-meel of te zware melen).   Herman kan het zelfs, dus ! \r\n \r\ntot met Pasen dan !\r\n    \r\nMieke    \r\n', 'mieke', ''),
(43, 'brugse flensjes', 'warm', '', '400 gr zelfrijzende bloem,1 liter volle melk,50 gr fijne kristalsuiker,2 pakjes vanillesuiker,6 eieren, 1 flesje brugs tarwebier,boter.', '6', 60, 30, 'nee', 'ja', 'nee', '', 'met bier', 'Kluts de melk,suiker,eieren,vanillesuiker en het bier samen in een grote beslagkom.\r\nZeef eral kloppend de bloem doorheen, en vergeet niet een snuifje zout toe te voegen.\r\nLaat een halfuurtje op een warme plaats rijzen\r\nZet twee beboterde koekenpannen op het vuur,giet een halve pollepel beslag per pan en laat mooi rondlopen\r\nLangs beide kant mooibruin bakken,\r\nOpdienen in voorverwarmde borden,flink bestrooien met poedersuiker,en voor de liefhebbers, besprenkel je er nog kriskras, met een soeplepel,chocolade of caramelsaus overheen.', 'kr w vl', ''),
(44, 'gewokte kalkoenfilets met oosterse groenten', 'warm', 'vlees', '2 kk filets,1 gele paprika,1 oranje paprika,sjalotfijngesnipperd,4 teentjes look fijngesnipperd,250 gr sluimererwten,200 gr sojascheuten,200 gr chinese kool,1 busseltje verse korianer,3 pijpjes bieslook fijngesnipperd,15 cl sojasaus,4 eetlepels olijfolie,cayennepeper,5 cl droge sherry,2 rijstbuiltjes\r\n', '4', 0, 30, 'nee', 'nee', 'nee', '', 'wok', 'de dag voordien: snij het vlees in blokjes. Leg het in een diepe kom en giet er de soyasaus en de sherry over,meng er de fijngesnipperde sjalot en lookteentjes onder en de cayennepeper, Laat het vlees zo 24 uur marineren.\r\nDe dag zelf: snij de chinees kool in fijne reepjes.Snij de sluimererwten in 2.\r\nVerhit 2 eetlepels olie in de wok en roerbak daarin de chineese kool,sluimererwten, de gele en oranje paprika en de soyascheuten.\r\nbak ze 3 tot 4 minuten tot ze beetgaar zijn.verhit 2 eetlepels olie in een tweede wokpan en schroei daarin het vlees aan.\r\n\r\nLaat 2 tot 3 minuten garen.\r\nBreng ondertussen water aan de kook en leg de builtjes rijst in het kokende water.Laat de rijst zolang koken als op de verpakking staat aangeduid.Mang de gewokte groenten bij het vlees.Strooi er de fijngesniperde bieslook over.\r\nGoed mengen Serveer met de rijdt.', 'tips', ''),
(42, 'karnemelkstovers met eieren', 'warm', 'andere', '1,250 kgr geschilde aardappelen,1/2 liter ongezoete karnemelk, 1 klontje boereboter,2 trostomaten,1 lepel gehakte peterselie,1 lepel gehakte bieslook,zeezout en p uit de molen,10 eieren.\r\n', '4', 60, 20, 'nee', 'ja', 'nee', '', 'ouderwets', 'Kook de aardappelen in licht gezouten water.Als ze gaar zijn,stamp ze tot puree.\r\nVoeg de boter ,de peterselie en de karnemelk erbij,meng goed. Bijkruiden naar smaak.\r\nStort de  stovers" ( = puree}  in een grote vuurvaste schotel.\r\nBak dit in een voorverwarmde oven gedurende 30 min op 150 Graden celsius\r\nKluts ondertussen de eieren samen met de in kleine blokjes gesneden tomaten\r\nHaal de schotel uit de oven,giet er de eiermengeling over,bestrooi met gehakte bieslook en plaats terug in de oven voor een tiental   minuutjes.\r\nMet een beetje handigheid en een lasagneschepper kan je nu mooie gelijke porties puree met een laagje ei erbovenop serveren.\r\n\r\n\r\n\r\nTip: je kan de stovers ook in kleine vuurvaste kommetjes per persoon maken. Geef er dikke, beboterde sneden boerenbrood bij. ', 'kr w vl', ''),
(45, 'knolselder roomsoep', 'warm', '', '1kleine knolselder,1 ajuin,1,5 liter water,3 dl witte wijn,2 kippenbouillonblokjes,potje room, peper zout,bosje bieslook, 1oo gr boter, 50 gr bloem', '4 tot 6', 60, 20, 'nee', 'ja', 'ja', '', 'selder', 'was,schil en snij de knolselder in fijne stukjes,evenals de ajuin.\r\nstoof de knolselder en ajuin in 100 gr boter\r\nvoeg het water en de wijn toe,samen met de bouillonblokjes en laat koken tot de groenten gaar zijn.\r\nmet de staafmixer heel fijn pureren.\r\nbreek de bloem in een kopje water,giet dit al roerend bij de soep en laat doorkoken tot er een lichte binding ontstaat.\r\ntenslotte roer je er twee eetlepels gehakte bieslook doorheen en laat de soep nog een minuutje zachtjes koken.\r\nbreng op smaak met peper en zout indien nodig.\r\n\r\nwanneer je slechts 1/3 van de hoeveelheid water gebruikt en dezelfde werkwijze hanteert,verkrijg je een prachtige roomsaus.\r\nmeng 100 gr garnalen,2 eetlepels room  en 2 eetlepels anijslikeur.verdeel dit in eierdopjes en geef per persoon zon dopje om onder de soep tre mengen.', 'kr w vl', ''),
(46, 'portosaus', 'warm', 'vlees', '2,5 dl porto, 2,5 gr wildfumet,1 sjalot,2 el mosterd,1 zakje kruidenboeket,2 el rode bessengelei,zout,peper', '4', 30, 30, 'nee', 'nee', 'nee', '', 'wild', 'de porto verhitten in een kleine kookpot met dikke bodem. aan de kook brengen, de temperatuur halveren en de bereiding voor een derde laten inkoken. de wildfumet,de sjalot, de mosterd en het kruidenboeket toevoegen. de vloeistof voor twee derden laten inkoken gedurende ongeveer 7 min. de rode bessengelei toevoegen. goed mengen.  met zout en peper kruiden. de bereiding verder laten inkoken gedurende 3 min. de saus zeven.\r\n', 'delhaize', ''),
(47, 'varkensoesters met blauwe druiven', 'warm', 'vlees', '4 varkensoesters,ongeveer 600 gr\r\n250 gr blauwe druiven,1 tl boter,20 cl zoete witte wijn, vb zoete bordeaux.\r\n2 el bruine suiker, 1 puntje gemberpoeder,schil van 1 sinaasappel,sap van 1 sinaasappel,1tl xeresazijn, 1 el fijngehakte rozemarijn, 1 el olifolie, z en p.takjes rozemarijn.', '4', 20, 30, 'nee', 'nee', 'nee', 'rode argentijnse wijn', '', 'zout,peper en rozemarijn mengen. de varkensoesters inwrijven met dit mengsel. de olie en de boter verhitten in een pan met zware bodem. de varkensoesters gedurende ongeveer 2 min aan elke kant bruinen. 2 min laten verder garen,omdraaien en nog eens 2 min laten bruinen.het varkensvlees moet lichtroos zijn aan de binnenkant. bewaren. de varkensoesters in alum papier wikkelen.\r\n het aanbaksel in de pan losmaken met de zoete witte wijn. de bruine suiker,het gemberpoeder, de sinaasappelschil en het sinaasappelsap toevoegen. goed mengen en 2 min verder laten garen op een matig vuurtje. de xeresazijn toevoegen en goed mengen. Kruiden indien nodig. de druiven toevoegen. gedurende 2 min laten garen. de varkensoesters in de saus leggen omdeze weer op te warmen gedurende 1 min.\r\neen varkensoester op elk bord leggen omringd door druiven. overgieten met de saus, direkt opdienen.Garneren met een takje rozemarijn, direkt opdienen.', 'delhaize', ''),
(48, 'kalkoenstoofpannetje', 'warm', 'vlees', '750 gr kalkoenfilets,200gr gedroogde pruimen,zonder pit. 1 pakje soepgroenten van 250 gr,2 uien, 1 tot 2 teentjes knoflook,1 en 1/2 dl droge sherry. 1,2 l gevogeltebouillon, 4 eetlepels olie, verse citroenmelisse, citroensap, p en z.', '4', 40, 30, 'ja', 'ja', 'ja', '', 'neiuwjaarsmenu', 'halveer de pruimen, laat ze 1 min opkoken inde sherry. \r\npel de uien en de knoflook en snipper ze fijn. snij de kalkoenfilets in blokjes, bak ze kort aan in hete olie, werk in porties.\r\nkruid met p en z en zet even aan de kant. \r\nstoof in  dezelfde pan  de uien,knoflook en de soepgroenten aan\r\ndoe er het vlees , de bakpruimen de sherry en de gevogeltefond bij.  laat nog een paar min zacht koken totde blokjes gaar zijn. breng op smaaak met citroensap. p en z.\r\nwerk af met gehakte citroenmelisse,\r\nserver met rijst of stokbrood.', 'gb', ''),
(49, 'oliebollen', 'warm', '', '1 kgr fijne patisseriebloem,75 gr verse gist, 7 dl lauwe melk,20 gr zout,1 ei,50 gr fijne suiker,poedersuiker\r\n', '4', 40, 40, 'nee', 'ja', 'nee', '', '', 'giet de bloem in een grote mengkom\r\nlos de gist op in de helft vd melk en roer die door de bloem\r\nmeg ei,zout en fijne suiker bij de rest vd melk en voeg dit al roerend bij de eerste mengeling\r\nklop alles goed dooreen tot je een glad beslag verkrijgt\r\nleg een vochtige doek over de kom en laat op een warme plaats minstens een uur  rijzen\r\nlet op : zorg ervoor dat je mengkom echt wel groot genoeg is, want het volume verdubbelt\r\nverwarm de frituurkete; op 180 graden c\r\nschep met behulp van 2 soeplepels die je in de frituurolie doopt,gelijke portietjes die je rechtstreeks in het hete vet deponeert.\r\nbak ze enkele minuutjes goudbruin, vergeet ze niet te keren\r\nleg de bollen daarna even op keukenpapier,leg ze vervolgens in een grote schaal en bestrooi met poiedersuiker\r\n\r\nbeste gist bij de warme bakker.\r\nlet op dat je de gist oplost in lauwe melk, niet warm of koud\r\n', 'krant wvl', ''),
(50, 'frisse aardappelsalade', 'koud', '', '700 gr aardappelen (kleine ricola of charlotte), 1potje room,1 potje ongesuikerde yoghurt,1 eetlepel honing,geraspte mierikswortel, 2 eetlepels gehakte bieslook en peterselie, koffielepeltje paprikapoeder en snuifje peper en zout.', '4 tot 6', 20, 20, 'nee', 'ja', 'nee', '', 'barbecue', 'was en kook de aardappelen in de schil\r\npel ze en laat afkoelen\r\nmaak in een grote slakom een dressing van alle overige ingredienten.\r\nsnij de aardappelen in dikke plakken en meng ze onder de dressing.\r\nafwerken met een beetje peterselie.\r\n\r\ntip 1 met aardappelen die je van de middagmaaltijd over hebt, maak je op die manier snel een snack voor  s avonds.\r\ntip 2 vervang ook de room deoor yoghurt en je snoept calorie arm..', 'kr w vl', ''),
(51, 'hoe bewaar ik verse gemberwortel?', '', '', '', '', 0, 40, 'nee', 'ja', 'ja', '', 'genber', 'ER ZIJN VERSCHILENDE MOGELIJKHEDEN. VERPAKT IN KEUKENPAPIER  houdt u gemberwortel in de groentelade van de koelkast ruim een week goed. wie hem langer wil bewqren doet het zo: schil hem en snij hem in grove stukken en doe ze in een bokaaltje. overgiet met droge sherry, sluit goe af. bij voorkeur met schroefdeksel en zet in de koelkast. \r\nde alcohol maakt dat de gemberworte; weken goed blijft. het sherryparfum doet het bovendien ook heel goed in oosterse gerechten', 'libelle', ''),
(52, 'vacherins', 'koud', '', '', '4', 0, 20, 'nee', 'ja', 'nee', '', 'schuimtaartjes met slagroom en aardbeien', 'verwarm de oven voor op 90 graden. bedek een grote bakplaat met bakpapier. klop 3 eiwitten stijf met een snuifje zout. strooi er 150 gr fijne suiker bij en blijf kloppen tot het eiwitschuim glanst en in pieken rechtop blijft staan. schep het in 4 porties op het bakpapier. zet 2 uur in het midden van de oven. laat afkoelen op het bakpapier. maak voorzichtig los. werk elke vacherin af met een flinke toef slagroom ( opgeklopt met vanillesuiker) , vers aardbeien en een blaadje munt. geef er eventueel nog wat extra aardbeiencoulis bij \r\n\r\n schuimpjes of meringues moeten eigenlijk ( opdrogen) ,niet echt bakken, vandaar de lange oventijd en de lage temperatuur', 'libelle', ''),
(53, 'warme dipsaus', 'warm', 'vis', '', '4', 0, 30, 'nee', 'nee', 'nee', '', 'dipsaus  ', 'laat op een zacht vuur een plakje boter van min of meer 2 cm smelten en doe er 8 fijngehakte teentjes knoflook en 2 blikjes ansjovis in stukjes bij.. roer tot de s\r\nansjovis wegsmelt< jawel> , schenk er dan 3 dl room bij en laat tot ongeveer de helft inkoken. \r\n\r\nlekker bij een salade of op een broodje rauwkost.', 'libelle ', ''),
(54, 'broccolisoepje met zalm    ', 'warm', 'vis', '1broccoli van 500 gr. 1 aardappel, 2 uien,1 preiwit, solo, 2 dl gevogeltebouillon,2 dl rppm, 1/2 bosje bieslook, p en z, 4 sneetjes gerookte zalm of forel.', '4', 30, 40, 'nee', 'ja', 'nee', '', 'zalm', 'spoel de broccoli en snij in grote stukken. schil de aardappel en snij in blokjes. pel en snipper cde uien. snipper het eiwit.\r\nverwarm wat solo en stoof er de groenten in aan. bevochtig met de gevogelte-bouillon en laat 45 minuten sudderen.\r\nklop de room half stijf. snipper de bieslook en meng bieslooksnippers onder de room.\r\nmix het broccolisoepje en breng op smaak met p en z.  snij de zalm of forel in fijne sliertjes.  leg in elk soepbord wat zalm - of forelsnipperss en giet er de kokende soep over.  leg er voorzichtig een lepeltje bieslookroom op.', 'kr wvl', ''),
(55, 'frisse haringtartaar', 'koud', 'vis', '4 nieuwe maatjes, goed gekoeld. 2 eetlepels kappertjes,kleine bokaaltjes in supermarkt.2 pijpajuintjes,2 eetlepels room,stuk geschilde komkommer van ongeveer 10 cm.p.z.en een snuifje bloemsuiker\r\n', '4', 0, 30, 'nee', 'nee', 'nee', 'witte ook bier', 'maatjes', 'week de kappertjes enkele uurtes in melk om te ontzouten.\r\nsnij de komkommer in de lengte doormidden, verwijder de zaden. strooi er zout op en laat een kwartiertje trekken. om te ontvochten.\r\ndep de komkommers droog met keukenpapier en snij de stukken in blokjes = brunoise.\r\nsnij de pijpajuintjes in ragfijne ringetjes.\r\nook de maatjes snij je in brunoise.\r\nmeng alles door elkaar met de room,breng op smaak met p.z. en poedersuiker.\r\nlaat alles minstens een uurtje in de koelkast rusten tot het door en door koel is.\r\nserveer met minibroodjes of met lookolie bestreken stokbrood.\r\n\r\n', 'kr  w vl', ''),
(56, 'gevulde ministar- tomaatjes', 'koud', 'vis', '', '4', 0, 20, 'nee', 'nee', 'nee', '', 'garnalen', 'snijd de bovenkant van de tomaatjes en hol ze voorzichtig uit met een lepeltje.met een scherpe rand. bestrooi de binnenkant met wat zout en laat ze omgekeerd uitdruppelen. vul op met grijze garnalen,stop er een bieslooksteeltje in en schik het hoedje weer op de tomaatjes. leg in elke lepel wat preischeutjes en schik daarop het gevilde tomaatje.', 'knack', ''),
(57, 'aspergepunten met dillesaus', 'warm', 'vis', '', '', 0, 40, 'nee', 'nee', 'nee', '', '', 'voor elk hapje heb je een verse,niet geschilde aspergepunt nodig. Blancheer de punten goed 5 min en snijd elke punt in de lengte doormidden.\r\nvoor het sausje laat je visfumet en witte wijn inkoken.Voeg room toe en laat verder inkoken tot een dikke saus. Kruid met p en z en fijngehakte dille. Leg in elke lepel een bodempje saus. Schik daarop twee halve aspergepuntjes,stukjes fijngeneden gerookte zalm en een takje dille.', 'knack', ''),
(58, 'kaas-komkommerhapjes', 'koud', 'vis', 'voor 8 hapjes\r\n', '4', 0, 30, 'nee', 'ja', 'nee', '', '', 'vermeng 25 gr verse kaas met 100 gr gerookte zalmreepjes,een geplet knoflookteentje,een gesnipperde uitjeen een koffielepel gehakte bieslook en basilicum.\r\nklop 1 dl room stijf en spatel de slagroom  luchtig door het kaasmengsel. zet in de koelkast om op te stijven.snij een komkommer in dikke stukken eventueel zigzaggend=haaietande,\r\nlepel er depitten uit en vul ze ment het mengsel werk af met foreleitjes.\r\n\r\n\r\nmet 2 stuks per persoon = frivool voorgerechtje.', 'libelle', ''),
(59, 'aperitiefhapje met gorgonzola', 'koud', 'vlees', '', '4', 0, 20, 'nee', 'ja', 'nee', '', '', 'meng gorgonzola met wat mascarpone en wat melk tot een romige mengeling. besmeer een plakje gekookte hesp  12 maal 15 cm met een koffielepel mengeling. maak een hespenrolletje en snij het in stukken van 2 tot 3 cm breed.', 'libelle', ''),
(60, 'lepelhapje uit de oven', 'warm', 'vis', 'klein potje mosselen op azijn.\r\n4 eetlepels room, 1 grote eetlepel boursin, gehakte bieslook,gemalen kaas.', '4', 0, 20, 'nee', 'nee', 'nee', '', 'hot mexican valentino', 'de mosselen afgieten en goed afspoelen. verdeel ze over porseleinen hapjeslepeltjes.\r\ngiet de room samen met de boursin in een kopje en zet dit 30 seconden in microgelfoven.\r\nverdeel het mengseltje over de mosselen en strooi er een beetje kaas en bieslook over.\r\nzet de lepels 5 minuten onder de grill en dien ze op met een dessertvorkje erbij,', 'kr w vl', 'geen tip'),
(61, 'brochette van staartvis met oregano', 'warm', 'vis', '2 kgr gefileerd staartvis = 11 maal 5 blokjes\r\n', '11', 0, 30, 'nee', 'nee', 'nee', '', '', 'verdeel afwisselend 600 gr in klompjes van 3 cm    en 16 verse laurierblaadjes. over de brochetten,\r\ndoop  ze in 20 cl droge witte wijn op smaak gebracht met 2 geperste knoflookteentjes.\r\nen dan gemixt in mengsel van\r\n2 sneden witbrood\r\n20 gr geraspte parmezan\r\n1 eetlepel fijngehakte sinaasappelschillen\r\n1eetlepel gedroogde oregano.\r\nbesprenkel met olijfolie en 10 min in bbq of oven', '', ''),
(62, 'Fazant met pompoenpuree', 'warm', 'vlees', '·  2 fazanten\r\n·  8 aardappelen\r\n·  1 sinaasappel\r\n·  1 steranijs\r\n·  1/4 pompoen\r\n·  4 schijfjes Ganda ham\r\n·  Vitelma Finesse\r\n·  1 ei\r\n·  Alpro Soja Cuisine\r\n·  platte peterselie\r\n', '4', 0, 40, 'nee', 'ja', 'ja', '', '', '·  Vraag de fazantjes ontbeend zodat we billetjes en borstjes hebben. Vraag ook de beenderen gehakt\r\n·  Bak de beenderen aan in Vitelma Finesse tot alle kantjes bruin zijn, bevochtig met water tot alles volledig onder zit. Laat alles voor de helft inkoken en bevochtig opnieuw. Laat opnieuw inkoken, nu tot er nog +/- 4 dl over is. Dit wordt de saus\r\n·  Kook de aardappelen met pompoen tot puree en voeg er wat Vitelma, eitje en wat Alpro Soja Cuisine aan toe\r\n·  Bak de fazantenborstjes in de Vitelma Finesse bruin tot ze gaar zijn, blus met wat jus, het sinaasappelsap en voeg de steranijs toe\r\n·  Bak de Gandaschijfjes tussen 2 bakplaten tot ze krokant zijn\r\n·  Serveer de fazant met de stoemp, jus en Ganda. Versier met peterselie\r\n', 'VTM lekker thuis', ''),
(63, 'rijsttaart 2', 'warm en koud', 'andere', '', '4', 0, 40, 'nee', 'nee', 'nee', '', 'makkelijke versie', 'rijsttaart: als je geen boss rijdt hebt kan je ook zelf een rijstbeslag= dessertrijst maken: je kan zelfs de dag voordien reeds je rijst hiervoor koken op volgende manier: kook hiervoor 100 gr rijst met 50 gr boter,3/4 liter melk en 75 gr griessuiker. goed roeren en niet te hard laten koken,regelmatig kijken en roeren dat het niet aanbakt.  dan laten afkoelen.\r\n \r\nheb aldus ook een eenvoudiger recept gevonden van rijsttaart.\r\nhier volgt het dan :\r\nvoor bakvorm van 24-26 cm diameter\r\nkooktijd/temperatuur: 30 min aan 200 celsius\r\n \r\ningredienten:1 liter melk, 150 gr griessuiker, 1 zakje vanillesuiker,150 fr rijst, 4 eieren,4 eetlepels zelfrijzende bloem,een beetje paneermeel, boter.\r\n \r\nbereiding:\r\n1/meng de melk,suiker en de rijst, en breng aan de kook.laat 25 min koken ( zoals boven beschreven: regelmatig roeren,niet te hard en kijken,bakt makkelijk aan ) ,laten afkoelen ( kan makkelijk daags voordien gebeuren)\r\n2/scheid de dooiers van de eiwitte. Meng de eierdooiers en de zelfrijzende bloem onder de rijst.\r\n3/klop eiwitten stijf en meng onder het rijstmengsel.\r\n4/bestrijk de bakvorm met boter en bestrooi met wat paneermeel.\r\n5/stort het rijstbeslag in de bakvorm en bak gaar.\r\nziezo,dat was mijn culinaire rubriek. ik duim voor je voor morgen en de', 'boerinnenbond', ''),
(65, 'aperitiefwafeltjes: palmhartje met gandaham', 'warm', 'vlees', '2 vellen bladerdeeg\n4 schijfjes gandaham\n2 eigelen\nbakken en braden', '4', 0, 30, 'nee', 'nee', 'nee', '', 'wafeltjes', 'Schik het bladerdeeg voor u en smeer in met eigeel\n\nLeg er de ham op en smeer deze ook in met eigeel\n\nPlooi nu beide randen naar het midden toe en duw ze tegen elkaar aan\n\nLeg deze rol in de diepvries voor een uurtje\n\nSnij nu in stukken van 1/2 cm, smeer de bakplaat in en leg ze op de bakplaat\n\nBak ze 25 minuten op 180 graden\n\n \n \n\n\n\n ', 'vtm', ''),
(66, 'palmhartjes met gandaham', 'warm', 'vlees', '', '4', 0, 30, 'nee', 'nee', 'nee', '', 'gandaham', 'Benodigdheden voor 4 personen:\r\n\r\n\r\n2 vellen bladerdeeg\r\n\r\n4 schijfjes gandaham\r\n\r\n2 eigelen\r\n\r\nbakken en braden\r\n\r\n\r\nBereiding:\r\n\r\n\r\nSchik het bladerdeeg voor u en smeer in met eigeel\r\n\r\nLeg er de ham op en smeer deze ook in met eigeel\r\n\r\nPlooi nu beide randen naar het midden toe en duw ze tegen elkaar aan\r\n\r\nLeg deze rol in de diepvries voor een uurtje\r\n\r\nSnij nu in stukken van 1/2 cm, smeer de bakplaat in en leg ze op de bakplaat\r\n\r\nBak ze 25 minuten op 180 graden\r\n\r\n\r\n \r\n \r\n\r\n\r\n\r\n ', 'vtm', ''),
(67, 'appelwafels met ricotta', 'warm', 'andere', '4 velletjes bladerdeeg (15 cm op 15 cm)\n\n1 Jonagold appel\n\n2 eetlepels bruine suiker\n\n4 eetlepels ricotta kaas\n\nwat munt', '4', 0, 40, 'nee', 'ja', 'nee', '', 'bladerdeeg', 'Leg 2 bladerdeeg velletjes voor u en schik er dikke schijven appel op (1 cm). Leg er het 2de bladerdeeg op, snij het in 4 en bak ze in het wafelijzertje\nMeng de ricotta met de bruine suiker\nSnij de rest van de appel in julienne\nServeer de wafeltjes met de ricotta en de julienne\nVersier met munt', 'vtm', ''),
(68, 'Croque monsieur met vis', 'warm', 'vis', '8 sneetjes brood\nwat ijsbergsla\n1 tomaat\n4 eetlepels maanzaad\n4 mooie schijfjes tonijn\n2 bolletjes mozzarella\n8 eetlepels sojasaus\n4 eetlepels Vandemoortele geroosterde sesamolie', '4', 0, 40, 'nee', 'ja', 'ja', '', '', 'Snij de mozzarella in schijfjes en marineer ze in de sojasaus\n\nSchik de sneetjes brood voor u en leg hierop wat gesneden ijsbergsla, wat gemarineerde mozzarella en tonijn. Overstrooi met maanzaad. Doe hierop de rest van de mozzarella dan de schijfjes tomaat en daarop het brood\n\nGiet wat geroosterde sesamolie op het brood en doe de croques in de croquemachine\n\nZie erop dat alles fijn gesneden is zodat de croque niet te dik wordt en nog in de machine kan', '', ''),
(70, 'ree met witloofchutney', 'warm', 'vlees', '', '', 0, 40, 'nee', 'ja', 'ja', '', '', '\r\nRee met witloofchutney\r\n\r\n\r\nBenodigdheden voor 4 personen:\r\n\r\n\r\n500 gr reefilet\r\n\r\n10 stonkjes witloof\r\n\r\n5 eetlepels suiker\r\n\r\n2 eetlepels rozijntjes\r\n\r\n2 sneetjes peperkoek\r\n\r\n4 eetlepels sherryazijn\r\n\r\n2 sneetjes ganzenlever\r\n\r\n1/2 fles porto\r\n\r\nwat Delicatesse Walnootolie\r\n\r\nwat kervel\r\n\r\nVandemoortele arachideolie\r\n\r\nVitelma Finesse\r\n\r\n\r\nBereiding:\r\n\r\n\r\nBak de reefilet langs beide kanten bruin en laat verder garen : à point , rose of bien cuit\r\n\r\nKook de porto in tot siroop\r\n\r\nHaal de blaadjes van het witloof en stoom ze gaar, spoel onder koud water en bak ze bruin in arachideolie met wat Vitelma Finesse. Overstrooi ze met suiker, blus met de azijn en voeg er de rozijntjes en de peperkoek in blokjes aan toe. Laat inkoken tot je een chutney bekomt\r\n\r\nServeer de reefilet in sneetjes, gewoon met wat braadjus, de chutney en de ingekookte porto. Schik een stukje ganzenlever op de chutney en besprenkel met wat Delicatesse Walnootolie\r\n\r\nVersier met wat kervel\r\n', 'vtm', ''),
(71, 'wilde eendeborstfilet met savooistoemp', 'warm', 'vlees', '', '', 0, 30, 'nee', 'ja', 'nee', '', '', ' \r\nWilde eendbrochette met koolpuree\r\n\r\n\r\nBenodigdheden voor 4 personen:\r\n\r\n\r\n4 wilde eendenborsten\r\n\r\n2 dl bruine fond\r\n\r\n2 eetlepels veenbessenconfituur\r\n\r\n4 eetlepels porto\r\n\r\n1 eetlepel Piet Huysentruyt peper\r\n\r\n2 schijfjes ganzenleverpastei\r\n\r\n4 dikke aardappelen\r\n\r\n4 schijfjes Gandaham\r\n\r\n1/4 savooikool\r\n\r\n1 dl Alpro Soya Cuisine\r\n\r\n2 eetlepels gerookte spekblokjes\r\n\r\nkervel\r\n\r\nAlpro bakken en braden\r\n\r\n\r\nBereiding:\r\n\r\n\r\nSnij de eendenborsten in stukjes van 3 cm en prik ze op een brochette. Bak ze in Alpro bakken en braden\r\n\r\nBlus de pan met de bruine fond, porto en voeg er de confituur aan toe. Laat tot de helft inkoken\r\n\r\nKook de aardappelen met de kool gaar, giet af en maak er een lekkere stoemp van met de Alpro Soya Cuisine en de gerookte spekblokjes\r\n\r\nSnij de Gandaham in julienne, schik op een boterpapier in een bakplaat en plaats +/- 120 minuten op 70 graden in de oven\r\n\r\nServeer de stoemp met daarop 1/2 schijfje ganzelever. Dan de brochette. Overstrooi met de Piet Huysentruyt peper en oversaus. Overstrooi met de gedroogde Gandaham\r\n\r\nVersier met kervel\r\n\r\n\r\n \r\n \r\n\r\n\r\n\r\n ', 'vtm', ''),
(72, 'kabeljauw met ratatouille in microgolfoven', 'warm', 'vis', '', '', 0, 30, 'nee', 'nee', 'nee', '', 'microgolfoven', 'Kabeljauw met ratatouille bereid in de microgolfoven\r\n\r\n\r\nBenodigdheden voor 4 personen:\r\n\r\n\r\n600 gr kabeljauw\r\n\r\n1 courgette\r\n\r\n1 aubergine\r\n\r\n4 tomaten\r\n\r\n2 teentjes look\r\n\r\n1 groene paprika\r\n\r\n12 blaadjes basilicum\r\n\r\n8 eetlepels olijfolie\r\n\r\nsolo\r\n\r\n\r\nBereiding:\r\n\r\n\r\nSnij de courgette, aubergine en paprika in fijne brunoise\r\n\r\nOntvel en ontpit de tomaten en snij ze in blokjes\r\n\r\nMeng ze met de brunoise en de gehakte look\r\n\r\nDoe alles in een ovenschaal en schik er 4 stukken kabeljauw op\r\n\r\nOvergiet het geheel met olijfolie en leg een klompje solo op de vis\r\n\r\nZet 8 minuten in de microgolfoven\r\n\r\nOverstrooi met de blaadjes basilicum\r\n\r\n\r\n \r\n', 'vtm', ''),
(73, 'lepelhapje met vis en honingsaus', 'warm', 'vis', '', '', 0, 20, 'nee', 'nee', 'nee', '', '', 'Lepelhapje : vis met citroen en honingsausje\r\n\r\n\r\nBenodigdheden voor 4 personen:\r\n\r\n\r\n2 tongfilets\r\n\r\n1 eetlepel honing\r\n\r\n2 eetlepels citroensap\r\n\r\n2 eetlepels water\r\n\r\n2 dl olijfolie\r\n\r\n4 sluimererwten\r\n\r\nkervel\r\n\r\n\r\nBereiding:\r\n\r\n\r\nZet de honing, het citroensap en het water op het vuur en laat voor de helft inkoken, werk op met wat olijfolie tot een gebonden saus en breng op smaak met peper en zout\r\n\r\nSnij de tongfilets in reepjes van 2 cm en stoom ze gaar\r\n\r\nStoom ook de sluimererwten gaar\r\n\r\nSchik de saus in de lepel, schik er de sluimererwt op, daarna de tongfilet en versier met wat kervel\r\n\r\n\r\n \r\n', 'vtm', ''),
(79, 'wokken met rundsvlees', 'warm', 'vlees', '', '', 0, 20, 'nee', 'nee', 'nee', '', 'wok', 'Wokken met rundsvlees\r\n\r\n\r\nBenodigdheden voor 4 personen:\r\n\r\n\r\n200 gr rundsvlees\r\n\r\n4 teentjes look\r\n\r\narachide-olie\r\n\r\ngehakte gember\r\n\r\nwat groentefond\r\n\r\n1 bot pijpajuin\r\n\r\n30 sluimerwten\r\n\r\n1 ajuin\r\n\r\n100 gr shii-take\r\n\r\noestersaus\r\n\r\n1 theelepel suiker\r\n\r\nsojasaus\r\n\r\nbalsamico\r\n\r\nkoriander\r\n\r\n\r\nBereiding:\r\n\r\n\r\nSnij het vlees fijn in reepjes\r\n\r\nMarineer het in de azijn, soja, oestersaus, suiker en gember\r\n\r\nSnij de ajuin, de sluimerwten, de pijpajuin en de shii-take in gelijke stukken\r\n\r\nSnij de look in schijfjes\r\n\r\nWarm de wok met de olie en bak de look tot hij goudbruin is\r\n\r\nWok de groenten en het vlees erbij\r\n\r\nBlus met wat groentefond en laat nog wat doorkoken\r\n\r\nServeer met rijst of noedels\r\n\r\n\r\n \r\n \r\n\r\n\r\n\r\n ', 'vtm', '');
